import {useState} from 'react';
import {quiz} from './soalan.js'
import './App.css';

const  Quiz = () => {
  const [activeQuestion, setActiveQuestion] = useState(0)
  const [selectedAnswer, setSelectedAnswer] = useState('  ')
  const [showResults, setShowResults] = useState(false)
  const [selectedAnswerIndex, setSelectedAnswerIndex] = useState(null)
  const [result, setResult] = useState( {
    score : 0,
    correctAnswers: 0,
    wrongAnswers: 0,
  })

  const { questions } = quiz
  const {question , choices , correctAnswer } = questions[activeQuestion]

  const onClickNext = () => {
    setSelectedAnswerIndex(null)
    setResult( (prev) => 
    selectedAnswer
    ? {
      ...prev,
      score: prev.score + 5,
      correctAnswers: prev.correctAnswers + 1,
    }
    : { ...prev, wrongAnswers:prev.wrongAnswers-1}
    )
    if (activeQuestion !== questions.length - 1) {
      setActiveQuestion((prev) => prev + 1)
    } else {
      setActiveQuestion(0)
      setShowResults(true)
    }
  }

  const onAnswerSelected = (answer, index) => {
    setSelectedAnswerIndex(index)
    if (answer === correctAnswer) {
      setSelectedAnswer(true)
    } else {
      setSelectedAnswer(false)
    }
  }


  return (<>
    <div className="quiz-container">
      {!showResults ? (
        <div>
          <div>
            <h1>Anniversary Quiz</h1>
          </div>
          <h2>{question}</h2>
          <ul>
            {choices.map((answer, index) => (
              <li
                onClick={() => onAnswerSelected(answer, index)}
                key={answer}
                className={selectedAnswerIndex === index ? 'selected-answer' : null}>
                {answer}
              </li>
            ))}
          </ul>
          <div className="flex-right">
            <button onClick={onClickNext} disabled={selectedAnswerIndex === null}>
              {activeQuestion === questions.length - 1 ? 'Finish' : 'Next'}
            </button>
          </div>
        </div>
      ) : (
        <div className="result">
          <h3>Result</h3>
          <p>
            There is no result but I believe you know me well hehe, and here is a letter for a year together
          </p>
          <p>
            Dear Khatijah,
          </p>
          <p>
            I can't believe we actually made it together for one year now ! (actually more).
            I honestly rasa happy, exciting selama kita bersama. We made alot of memories from 
            travelling the world together and doing things we never did. There
          </p>
        </div>
      )}
    </div>
    </>
  )
}

export default Quiz
